function calculateCenter(field,str){if(str.length>0&&str.length<13){document.getElementById(field).style.left=52-str.length/2;document.getElementById(field).innerHTML=str;return true}else{return false}}function submitForm(){if(calculateCenter("store_text_shirt",document.getElementById("text").value)){var currentdate=new Date;var datetime=currentdate.getFullYear()+"-"+(currentdate.getMonth()+1)+"-"+currentdate.getDate()+" "+currentdate.getHours()+":"+(currentdate.getMinutes()<10?"0":"")+currentdate.getMinutes();calculateCenter("customer_text_shirt",document.getElementById("text").value);document.getElementById("store_text").innerHTML="<h4>"+document.getElementById("text").value+"</h4>";document.getElementById("customer_text").innerHTML="<h4>"+document.getElementById("text").value+"</h4>";document.getElementById("store_color").innerHTML="<h4>"+document.getElementById("color").value+"</h4>";document.getElementById("customer_color").innerHTML="<h4>"+document.getElementById("color").value+"</h4>";document.getElementById("store_font").innerHTML="<h4>"+document.getElementById("font").value+"</h4>";document.getElementById("customer_font").innerHTML="<h4>"+document.getElementById("font").value+"</h4>";document.getElementById("store_size").innerHTML="<h4>"+document.getElementById("size").value+"</h4>";document.getElementById("customer_size").innerHTML="<h4>"+document.getElementById("size").value+"</h4>";document.getElementById("store_time").innerHTML="<h4>"+datetime+"</h4>";document.getElementById("customer_time").innerHTML="<h4>"+datetime+"</h4>";$("#print").show();return true}else{$("#error").show();document.getElementById("text").focus();setTimeout(function(){$("#error").hide()},5e3);return false}}function printForm(){if(submitForm()){$("#form").hide();$("#print_page").show();window.print();$("#print_page").hide();$("#form").show()}}$(document).ready(function(){$(window).keydown(function(event){if(event.keyCode==13){event.preventDefault();printForm()}})});